import { useState } from 'react';
import './App.css';
import Card from './Components/Card';
import Search from './Components/Search';
import NoResult from './Components/NoResult';


function App() {

  const [movieName, setMovieName] = useState("");
  const [movieList, setMovieList] = useState([
    {
      "Title": "Lauf um Dein Leben - Vom Junkie zum Ironman",
      "Year": "2008",
      "imdbID": "tt0954542",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BYzA3YjNjZDAtYWU2MC00YmIyLTkzMjEtOGE5YmIyZmYxODIzXkEyXkFqcGc@._V1_SX300.jpg"
  },
  {
      "Title": "Ivan Ironman Stewart's Super Off Road",
      "Year": "1989",
      "imdbID": "tt0162408",
      "Type": "game",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZDMwMWUyNDAtZmM3Ny00ZGM2LTg5MTItYmZiNzM1NzAyNGJmXkEyXkFqcGdeQXVyNzg5OTk2OA@@._V1_SX300.jpg"
  },
  {
      "Title": "Darasingh: Ironman",
      "Year": "1964",
      "imdbID": "tt0231426",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZTVkMmE1OWYtMDdhZC00Mjg2LWEzNzktMWZiMjAxMmU0YTM0XkEyXkFqcGdeQXVyODQwMDcwNDY@._V1_SX300.jpg"
  },
  {
      "Title": "Wild Man to Ironman",
      "Year": "2018",
      "imdbID": "tt9823994",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BZjI1NmUxOTctOTNjNi00ODQ0LTk2ZGUtMDM5YjEzNmM2NDM5XkEyXkFqcGc@._V1_SX300.jpg"
  },
  {
      "Title": "Ironman Triathlon World Championship",
      "Year": "2002",
      "imdbID": "tt10239424",
      "Type": "movie",
      "Poster": "https://m.media-amazon.com/images/M/MV5BMzcxZTIzNjUtZWEwYi00NWU0LTg2MzctZTBmZjNjNmZiNzZlXkEyXkFqcGdeQXVyMjcxMjE1OTM@._V1_SX300.jpg"
  },
  {
    "Title": "Superman, Spiderman or Batman",
    "Year": "2011",
    "imdbID": "tt2084949",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BMjQ4MzcxNDU3N15BMl5BanBnXkFtZTgwOTE1MzMxNzE@._V1_SX300.jpg"
  },
  {
    "Title": "The Amazing Spiderman 2 Webb Cut",
    "Year": "2021",
    "imdbID": "tt18351128",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BNzI0MmQyMzYtZDAzNi00ZWZiLWFjMTgtNzQwOTRjYTFlM2Y3XkEyXkFqcGc@._V1_SX300.jpg"
},
{
    "Title": "Spiderman the Verse",
    "Year": "2019–",
    "imdbID": "tt12122034",
    "Type": "series",
    "Poster": "https://m.media-amazon.com/images/M/MV5BNjA2NmZhOGEtZTQ5OS00MDI0LTg4N2UtYTRmOTllM2I2NDlhXkEyXkFqcGdeQXVyNTU4OTE5Nzc@._V1_SX300.jpg"
},{
  "Title": "Spiderman in Cannes",
  "Year": "2016",
  "imdbID": "tt5978586",
  "Type": "movie",
  "Poster": "https://m.media-amazon.com/images/M/MV5BZjc4MDYyMWQtNjM5MS00NzQxLTg5MTktMjI1MTVmNDNmNTA4XkEyXkFqcGc@._V1_SX300.jpg"
}
  ]);
  const [found, setFound]=useState("True");

  const getMovies = (movie) => {

    fetch(`http://www.omdbapi.com/?apikey=e8c5f6dd&s=${movie}`)
      .then(response => response.json())
      .then(data => { 
        if(data.Response==="False")
          setFound(data.Response)
        else{
          setFound("True");
          setMovieList(data.Search);
        }
      })
      .catch(err => console.log(err.message));
  }

  const setMovie = (e) => {
    setMovieName(e.target.value);
  }

  const getMovieList = (e) => {
    if(e.key==='Enter'){
      getMovies(movieName);
    }
  }

  return (
    <>
      <div className='main_container'>
      <div className="container my-3">
        <Header />
        <Search value={movieName} onChange={setMovie} onKeyPress={getMovieList}/><br/>
        <div className='movie_list container'>
          {
            (found==="False")?(<NoResult image={"../icon.jpg"}/>):movieList?.map((value, index) => (
              <Card prop={value} key={index} />
            ))
          }
        </div>
      </div>
      </div>
    </>
  );
}

export default App;
